package org.pxcbpmisframework.core.common.datasource;import java.util.Map;import org.apache.log4j.Logger;import org.springframework.jdbc.datasource.lookup.AbstractRoutingDataSource;import org.springframework.jdbc.datasource.lookup.DataSourceLookup;public class DynamicDataSource extends AbstractRoutingDataSource {	private static final Logger logger = Logger			.getLogger(DynamicDataSource.class);	protected Object determineCurrentLookupKey() {		logger.info("***************** 切换数据源中   *****************");		String dataSourceName = DataSourceContextHolder.getDataSourceType();		if (dataSourceName == null) {			dataSourceName = DataSourceType.values()[0].toString();		}		logger.info("***************** 正在使用的数据源：[" + dataSourceName + "]");		return dataSourceName;	}	public void setDataSourceLookup(DataSourceLookup dataSourceLookup) {		super.setDataSourceLookup(dataSourceLookup);	}	public void setDefaultTargetDataSource(Object defaultTargetDataSource) {		super.setDefaultTargetDataSource(defaultTargetDataSource);	}	public void setTargetDataSources(Map<Object, Object> targetDataSources) {		super.setTargetDataSources(targetDataSources);	}}